import React, { useState } from "react";

function Footer() {
  const [color, setColor] = useState([
    {
      name: "button1",
      color: "#0d1435",
    },
    {
      name: "button2",
      color: "#0d1435",
    },
    {
      name: "button3",
      color: "#0d1435",
    },
    {
      name: "button4",
      color: "#0d1435",
    },
    {
      name: "button5",
      color: "#0d1435",
    },
  ]);

  const colorHandel = (name) => {
    for (var i=0; i<color.length; i++) {
      if (color[i].name === name) {
        var arr = color;
        arr[i].color= "#fabb27";
        setColor(arr);
        break;
      }
      else{
        console.log("unvalid")
      }
    }
  };

  return (
    <footer>
      <button
        style={{ color: color[0].color }}
        className="allbuttons"
        onClick={colorHandel(color[0].name)}
        name="button1"
      >
        <i className="far fa-heart footerbutton"></i>
        <p>Favorite</p>
      </button>
      <button
        style={{ color: color[1].color }}
        className="allbuttons"
        onClick={colorHandel(color[1].name)}
        name="button2"
      >
        <i className="fas fa-tag footerbutton"></i>
        <p>Stock</p>
      </button>
      <button
        className="home-button"
        onClick={colorHandel(color[2].name)}
        name="button3"
      >
        <i className="fas fa-home footerbutton"></i>
      </button>
      <button
        style={{ color: color[3].color }}
        className="allbuttons"
        onClick={colorHandel(color[3].name)}
        name="button4"
      >
        <i className="fas fa-shopping-cart footerbutton"></i>
        <p> My cart</p>
      </button>
      <button
        style={{ color: color[4].color }}
        className="allbuttons"
        onClick={colorHandel(color[4].name)}
        name="button5"
      >
        <i className="fas fa-user-circle footerbutton"></i>
        <p>Account</p>
      </button>
    </footer>
  );
}

export default Footer;
